import React, { useState } from 'react';
import './Pdfdisp.css';
import { Uploader } from "uploader";
import { UploadDropzone } from "react-uploader";
import { extract } from '@extractus/article-extractor'

function PdfChat() {
  const [uploadedFileUrl, setUploadedFileUrl] = useState("");

  const handleComplete = async (files) => {
    if (files.length > 0) {
      const file = files[0];
      const reader = new FileReader();
  
      reader.onload = async (event) => {
        const buffer = event.target.result;
        
        // Now you can use `buffer` as needed, e.g., pass it to your extraction function
        const extractedText = await extract(buffer);
        console.log(extractedText);
      };
  
      reader.readAsArrayBuffer(file);
    }
  };

  return (
    <div className="pdf-div">
      <div className="main-heading">Welcome to ChatPDF Application</div>
      <UploadDropzone
        uploader={Uploader({ apiKey: "public_FW25c5Z7W7hFTqdUY9USFQFpmpMh"})}
        options={{ multi: true }}
        onUpdate={handleComplete}
        width="600px"
        height="375px"
      />
    </div>
  );
}

export default PdfChat;
