import React, { useState } from 'react';
import { getDocument } from 'pdfjs-dist';
import 'pdfjs-dist/build/pdf.worker.entry';
import './Pdfdisp.css';

function PdfChat({ setText }) {
  const [selectedFile, setSelectedFile] = useState(null);
  const [fileInfo, setFileInfo] = useState(null);

  const handleFileChange = (event) => {
    const file = event.target.files[0];

    if (file && file.type === 'application/pdf') {
      setSelectedFile(file);
      setFileInfo({
        name: file.name,
        size: file.size,
        type: file.type,
        lastModified: file.lastModified,
      });
    } else {
      alert('Please upload a valid PDF file');
    }
  };

  const handleSubmit = async () => {
    if (selectedFile) {
      console.log('File Information:', fileInfo);
      console.log('Selected File Object:', selectedFile);

      const arrayBuffer = await selectedFile.arrayBuffer();
      const pdf = await getDocument({ data: arrayBuffer }).promise;

      let extractedText = '';

      for (let i = 1; i <= pdf.numPages; i++) {
        const page = await pdf.getPage(i);
        const textContent = await page.getTextContent();
        const pageText = textContent.items.map(item => item.str).join(' ');
        extractedText += pageText + '\n';
      }

      setText(extractedText); // Set the extracted text in the parent component's state
      console.log('Extracted Text:', extractedText);
    } else {
      alert('No file selected');
    }
  };

  return (
    <div className="pdf-div">
      <div className="main-heading">Welcome to ChatPDF Application</div>
      <input className="file-input" type="file" accept="application/pdf" onChange={handleFileChange} />
      <button className="file-sbm-btn" onClick={handleSubmit}>Submit</button>
      {fileInfo && (
        <div className="file-info">
          <p><strong>File Name:</strong> {fileInfo.name}</p>
          <p><strong>File Size:</strong> {fileInfo.size} bytes</p>
          <p><strong>File Type:</strong> {fileInfo.type}</p>
          <p><strong>Last Modified:</strong> {new Date(fileInfo.lastModified).toLocaleString()}</p>
        </div>
      )}
    </div>
  );
}

export default PdfChat;
