import React, { useState } from 'react';
import './Pdfdisp.css';
import { Uploader } from "uploader";
import { UploadDropzone } from "react-uploader";
import { extract } from '@extractus/article-extractor'
import { PDFExtract } from 'pdf-extract';


function PdfChat() {

  const pdfExtract = new PDFExtract();
  const [uploadedFileUrl, setUploadedFileUrl] = useState("");


  const extractTextFromPDFUrl = async (pdfUrl) => {
    try {
      // Fetch the PDF file from the URL
      const response = await fetch(pdfUrl);
      const pdfData = await response.arrayBuffer();
  
      // Extract text from the PDF data
      const result = await pdfExtract.extractBuffer(pdfData, {});
  
      return result.text; // Extracted text
    } catch (error) {
      console.error('Error extracting text:', error);
      return null;
    }
  };










  const handleComplete = async (files) => {
    if (files.length > 0) {
      const input = files[0].fileUrl;
      console.log(input);
      setUploadedFileUrl(input);
  
      try {
        const extractedText = await extractTextFromPDFUrl(input);
        console.log('Extracted text:', extractedText);
        // Use the extracted text in your React component or perform other actions
      } catch (error) {
        console.error('Error extracting text:', error);
      }
    }
  };
  

  return (
    <div className="pdf-div">
      <div className="main-heading">Welcome to ChatPDF Application</div>
      <UploadDropzone
        uploader={Uploader({ apiKey: "public_FW25c5Z7W7hFTqdUY9USFQFpmpMh"})}
        options={{ multi: true }}
        onUpdate={handleComplete}
        width="600px"
        height="375px"
      />
    </div>
  );
}

export default PdfChat;
