import React, { useState } from 'react';
import './Chat.css';

function Chat() {
    const [inputValue, setInputValue] = useState('');
    const [chatHistory, setChatHistory] = useState([]);

    const handleInputChange = (event) => {
        setInputValue(event.target.value);
    };

    const handleClick = async () => {
        // Pass the inputValue to your Chat function or handle it here
        console.log("Input value: ", inputValue);

        // const updatedChatHistory = [
        //     ...chatHistory,
        //     { role: "user", parts: [{ text: inputValue }] },
        // ];
        // setChatHistory(updatedChatHistory);

        const msg = inputValue;
        const options = {
            method: 'POST',
            body: JSON.stringify({
                history: chatHistory,
                message: msg
            }),
            headers: {
                'Content-Type': 'application/json'
            }
        };

        try {
            const response = await fetch('http://localhost:8000/gemini', options);
            if (!response.ok) {
                throw new Error('Network response was not ok');
            }
            const data = await response.text();
            console.log(data)
            // Optionally, update chat history with the response
            // setChatHistory(updatedChatHistory=>[...updatedChatHistory,{role:"user",parts:[{ text: inputValue }]},{role:"model",parts:[{ text: data }]}]);
            setChatHistory(prevChatHistory => [...prevChatHistory, { role: "user", parts: [{ text: inputValue }] }, { role: "model", parts: [{ text: data }] }]);
        } catch (error) {
            console.error('There has been a problem with your fetch operation:', error);
        }

        // Clear the input field
        setInputValue("");
    };

    return (
        <>
            <div className="chat-box">
                <div className="chat-show">
                {chatHistory.map((entry, index) => (
                <div key={index} className={`chat-message ${entry.role}`}>
                    {entry.parts.map((part, partIndex) => (
                        <span key={partIndex}>{part.text}</span>
                    ))}
                    </div>
                ))}
                </div>
                <div className="input_div">
                    <input 
                        className="input_field" 
                        value={inputValue} 
                        placeholder='Hey Google tell me the latest news'
                        onChange={handleInputChange} 
                    />
                    <button className="arrow" onClick={handleClick}>
                        <img src="/send.png" alt='Here' />   
                    </button>
                </div>
            </div>
        </>
    );
}

export default Chat;
