import React, { useState } from 'react';
import './Pdfdisp.css';
import { Uploader } from "uploader";
import { UploadDropzone } from "react-uploader";
import { extract } from '@extractus/article-extractor'
import * as pdfjsLib from 'pdfjs-dist/build/pdf';
import * as pdfjsWorker from 'pdfjs-dist/build/pdf.worker.entry';

pdfjsLib.GlobalWorkerOptions.workerSrc = pdfjsWorker;
pdfjsLib.GlobalWorkerOptions.workerSrc = 'https://cdnjs.cloudflare.com/ajax/libs/pdf.js/2.11.338/pdf.worker.min.js';

function PdfChat() {
  const [uploadedFileUrl, setUploadedFileUrl] = useState("");

  const handleComplete = async (files) => {
    if (files.length > 0) {
      const input = files[0].fileUrl;
      console.log(input);
      setUploadedFileUrl(input);
  
      try {
        const loadingTask = pdfjsLib.getDocument(input);
        const pdf = await loadingTask.promise;
        const numPages = pdf.numPages;
        let text = '';
  
        for (let i = 1; i <= numPages; i++) {
          const page = await pdf.getPage(i);
          const pageText = await page.getTextContent();
          text += pageText.items.map(item => item.str).join(' ');
        }
  
        console.log(text);
      } catch (err) {
        console.error(err);
      }
    }
  };

  return (
    <div className="pdf-div">
      <div className="main-heading">Welcome to ChatPDF Application</div>
      <UploadDropzone
        uploader={Uploader({ apiKey: "public_FW25c5Z7W7hFTqdUY9USFQFpmpMh"})}
        options={{ multi: true }}
        onUpdate={handleComplete}
        width="600px"
        height="375px"
      />
    </div>
  );
}

export default PdfChat;
