import './Sidebar.css';
import Zoom from 'react-reveal/Zoom';

function Sidebar({chatHistory}){
    return(
       <div className='Sidebar'>
            <div className='sidebar_head'>Prompts <button><img src="/menu.png" alt='Here'></img></button></div>
            
            <div className="chat-show-side">
                    {chatHistory.map((entry, index) => (
                        <Zoom key={index}>
                            <div className={`chat-messages ${entry.role}`}>
                                {entry.parts.map((part, partIndex) => (
                                    <span key={partIndex}>{part.text}</span>
                                ))}
                            </div>
                        </Zoom>
                    ))}
                </div>
       </div> 
    )
}

export default Sidebar;
