import React, { useState } from 'react';
import './Pdfdisp.css';
import { Uploader } from "uploader";
import { UploadDropzone } from "react-uploader";

function PdfChat() {
  const [uploadedFileUrl, setUploadedFileUrl] = useState("");

  const handleComplete = (files) => {
    if (files.length > 0) {
      const fileUrl = files[0].fileUrl;
      setUploadedFileUrl(fileUrl);
    }
  };

  return (
    <div className="pdf-div">
      <div className="main-heading">Welcome to ChatPDF Application</div>
      <UploadDropzone
        uploader={Uploader({ apiKey: "free" })}
        options={{ multi: true }}
        onUpdate={handleComplete}
        width="600px"
        height="375px"
      />
    </div>
  );
}

export default PdfChat;
