import React, { useState } from 'react';
import './Pdfdisp.css'
import { Uploader } from "uploader"; // Installed by "react-uploader".
import { UploadDropzone } from "react-uploader";
import { UploadButton } from "react-uploader";
import { readFile } from 'node:fs/promises'
import { getTextExtractor } from 'office-text-extractor'


async function PdfChat(){

  // Initialize once (at the start of your app).
  const uploader = Uploader({
    apiKey: "free" // Get production API keys from Bytescale
  });
  


  // Configuration options: https://www.bytescale.com/docs/upload-widget/frameworks/react#customize
const options = { multi: true };


const [uploadedFiles, setUploadedFiles] = useState([]);

const handleComplete = (files) => {
  setUploadedFiles(files);
  alert(files.map((x) => x.fileUrl).join("\n"));
};


  // To extract the text from pdf files
  if (uploadedFiles){
    const extractor = getTextExtractor()
    const url = '${uploadedFiles}'
    const text = await extractor.extractText({ input: url, type: 'url' })
    console.log(text);
  }
  



  return (
    <div className="pdf-div">
      <div className="main-heading">Welcome to ChatPDF Application</div>
      <UploadDropzone uploader={uploader}
                  options={options}
                  onUpdate={handleComplete}
                  width="600px"
                  height="375px" />
    </div>
  );
}

export default PdfChat;
